import { useMemo, useContext, useCallback } from 'react';
import { campaignsData } from '../__mocks__/googleAdsMock';
import { setFilter, setState } from '../store/actions';
import DashboardContext from '../contexts/generalContext';

export default function useGoogleAds() {
    const [state, dispatch] = useContext(DashboardContext);
    const { filters } = state;

    const costData = useMemo(() => {
        const data = campaignsData.campanas.reduce((acc, campaign) => {
            const cost = campaign.costo;
            if (cost <= 100) {
                acc["<=100"]++;
            } else if (cost >= 101 && cost <= 250) {
                acc["101-250"]++;
            } else if (cost >= 251 && cost <= 400) {
                acc["251-400"]++;
            } else if (cost >= 401) {
                acc["401+"]++;
            }
    
            return acc;
        }, {
            "<=100": 0,
            "101-250": 0,
            "251-400": 0,
            "401+": 0
        });
        let items = [];
        Object.entries(data).forEach(([key, value]) => {
            items.push({
                value,
                name: key
            });
          })
        return {
            title: {
                text: 'Costo',
                subtext: 'por campaña',
                left: 'center'
              },
              tooltip: {
                trigger: 'item'
              },
              legend: {
                orient: 'vertical',
                left: 'left'
              },
              series: [
                {
                  name: 'Costo',
                  type: 'pie',
                  radius: '50%',
                  data: items,
                  emphasis: {
                    itemStyle: {
                      shadowBlur: 10,
                      shadowOffsetX: 0,
                      shadowColor: 'rgba(0, 0, 0, 0.5)'
                    }
                  }
                }
              ]
        }
    }, [filters]);

    return {
        costData
    }
}
