import { useMemo } from 'react';
import { googleAnaliticsMock } from '../__mocks__/googleAnalitisMock';

export default function useNewReleases() {

    const visitsDataChart = useMemo(() => {
        return {
            title: {
                text: 'Visitas a la pagina'
            },
            tooltip: {},
            xAxis: {
                type: 'category',
                data: googleAnaliticsMock.vistasPagina.map(item => item.fecha)
            },
            yAxis: {
                type: 'value'
            },
            series: {
                type: 'bar',
                data: googleAnaliticsMock.vistasPagina.map(item => item.vistas)
            }
        }
    }
        , [googleAnaliticsMock]);

    const sesionesChartData = useMemo(() => ({
        title: {
            text: 'Sesiones',
            x: 'center'
        },
        xAxis: {
            type: 'category',
            data: googleAnaliticsMock.sesiones.map(item => item.fecha)
        },
        yAxis: {
            type: 'value'
        },
        tooltip: {},
        series: [
            {
                name: 'Tasa de rebote',
                data: googleAnaliticsMock.sesiones.map(item => ({
                    value: item.sesiones,
                    name: item.tasaRebote
                })),
                type: 'line',
                symbol: 'triangle',
                symbolSize: 20,
                lineStyle: {
                  color: '#5470C6',
                  width: 3,
                  type: 'dashed'
                },
                itemStyle: {
                  borderWidth: 1,
                  borderColor: '#EE6666',
                  color: 'yellow'
                },

              }
        ]
    }), [googleAnaliticsMock])

    return {
        visitsDataChart,
        sesionesChartData,
        // radarChartData
    }
}
