import headers from './headers';
import { formatBodyUrlEncoded } from '../../utils/format';
import { clientID, clientSecret } from '../../constants/api-constants';

const connection = 'https://accounts.spotify.com/api';

const api = {
    autentication: {
        getToken() {
            var details = {
                'grant_type': 'client_credentials',
                'client_id': clientID,
                'client_secret': clientSecret
            };
            const url = `${connection}/token`;
            headers.post_urlencoded.body = formatBodyUrlEncoded(details);
            return fetch(url, headers.post_urlencoded);
        }
    },
    tracks: {
        newReleases() {

        }
    }
};

export default api;
