import React, { useContext } from 'react';
import {
    MenuFoldOutlined,
    MenuUnfoldOutlined,
} from '@ant-design/icons';
import { Button, Menu } from 'antd';
import routes from '../../constants/routes';
import DashboardContext from '../../contexts/generalContext';
import { items } from '../../constants/menu';
import useSidebar from '../../hooks/useSidebar';
import './style.scss';

const Sidebar = () => {
    const [state, dispatch] = useContext(DashboardContext);

    const {
        collapsed,
        toggleCollapsed,
        changeComponent,
        actualComponent,
    } = useSidebar();

    return (
        <div className='container-sidebar'>
            <div className='container-sidebar_header'>
                <Button type="primary" onClick={toggleCollapsed} className='container-sidebar_header_btn'>
                    {collapsed ? <MenuUnfoldOutlined /> : <MenuFoldOutlined />}
                </Button>
            </div>
            <div className='container-sidebar_body'>
                <Menu
                    defaultSelectedKeys={['GOOGLE_ANALITICS']}
                    mode="inline"
                    theme="dark"
                    inlineCollapsed={collapsed}
                    items={items}
                    onClick={(e) => changeComponent(e.key)}
                />
            </div>
            <div className='container-sidebar_body-container'>
                {routes[actualComponent].component}
            </div>
        </div>
    );
};

export default Sidebar;
