import React from 'react';
import { Chart } from '../../';
import useCRM from '../../../hooks/useCRM';
import { Table } from "antd";
import { columnsGoogleAds } from '../../../constants/constants';
// import Filters from './filters';
import './style.scss';

const CRM = () => {
    const {
        CostAdquisitionData,
        filterCrm,
        topcmdData
    } = useCRM();
    return(
        <div>
        {/* <Filters /> */}
        <div className="meta-charts-container">
            <div>
                <Chart options={costData} />
            </div>
            <div>
                <Chart options={topMetaAdsData} />
            </div>
            <div>
                <Table dataSource={filterMetaCampains} columns={columnsGoogleAds} />;
            </div>
            <div>
                <Chart options={topGastoPublicidadData} />
            </div>
        </div>
    </div>
    )
}

export default CRM;
