import React, { useState, useContext } from 'react';
import {
    MenuFoldOutlined,
    MenuUnfoldOutlined,
} from '@ant-design/icons';
import { Button, Menu } from 'antd';
import routes from '../../constants/routes';
import DashboardContext from '../../contexts/generalContext';
import { items } from '../../constants/menu';
import './style.scss';

const Sidebar = () => {
    const [state, ] = useContext(DashboardContext);
    const [collapsed, setCollapsed] = useState(false);
    console.log("el state", state)
    const toggleCollapsed = () => {
        setCollapsed(!collapsed);
    };

    return (
        <div className='container-sidebar'>
            <div className='container-sidebar_header'>
                <Button type="primary" onClick={toggleCollapsed} style={{ marginBottom: 16 }}>
                    {collapsed ? <MenuUnfoldOutlined /> : <MenuFoldOutlined />}
                </Button>
            </div>
            <div className='container-sidebar_body'>
                <Menu
                    defaultSelectedKeys={['1']}
                    defaultOpenKeys={['sub1']}
                    mode="inline"
                    theme="dark"
                    inlineCollapsed={collapsed}
                    items={items}
                />
            </div>
            <div className='container-sidebar_body-container'>
                {routes[state.actualComponent].component}
            </div>
        </div>
    );
};

export default Sidebar;
