import { useMemo, useEffect, useCallback, useContext } from 'react';
import DashboardContext from '../contexts/generalContext';
import { getNewReleases } from '../services/provider/tracks';
import { setState } from '../store/actions';

export default function useNewReleases() {
    const [state, dispatch] = useContext(DashboardContext);
    const { token, newReleasesItems } = state;

    const newReleases = useMemo(() => {
        const idsTracks = newReleasesItems.map(item => item.id);
    }, [newReleasesItems, token]);

    const getNewReleasesData = useCallback(async () => {
        const { albums } = await getNewReleases(token);
        dispatch(setState({ newReleasesItems: albums.items }));
    }, [token]);

    useEffect(() => {
        getNewReleasesData();
    }, [token]);

    return {
        newReleases
    }
}
